import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const parts = {
    part1 : 'Fundamentals of React',
    exercises1 : 10,
    part2 : 'Using props to pass data',
    exercises2 : 7,
    part3 : 'State of a component',
    exercises3 : 14
  }

  return (
    <div>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />
    </div>
  )
}

const Header = (props) => (
  <>
    <h1>{props.course}</h1>
  </>
)

const Content = (props) => (
  <>
    <Part part={props.parts.part1} ex={props.parts.exercises1} />
    <Part part={props.parts.part2} ex={props.parts.exercises2} />
    <Part part={props.parts.part3} ex={props.parts.exercises3} />
  </>
)

const Total = (props) => (
  <>
    <p>Number of exercises {props.parts.exercises1 + props.parts.exercises2 + props.parts.exercises3}</p>
  </>
)

const Part =(props)=>(
  <p>{props.part}{props.ex}</p>
)
export default App
