import React from 'react'

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }


  return (
    <div>
      <Header course={course.name} />
      <Content parts={course.parts} />
      <Total parts={course.parts} />
    </div>
  )
}

const Header = (props) => (
  <>
    <h1>{props.course}</h1>
  </>
)

const Content = (props) => (
  <>
    <Part part={props.parts[0].name} ex={props.parts[0].exercises} />
    <Part part={props.parts[1].name} ex={props.parts[1].exercises} />
    <Part part={props.parts[2].name} ex={props.parts[2].exercises} />
  </>
)

const Total = (props) => (
  <>
    <p>Number of exercises {props.parts.exercises1 + props.parts.exercises2 + props.parts.exercises3}</p>
  </>
)

const Part =(props)=>(
  <p>{props.part}{props.ex}</p>
)
export default App
